/// totalCinemaCount : 258
/// totalComingMovie : 27
/// totalHotMovie : 42

class HotPlayMoviesModel {
  int _count;
  List<Movies> _movies;
  int _totalCinemaCount;
  int _totalComingMovie;
  int _totalHotMovie;

  int get count => _count;

  List<Movies> get movies => _movies;

  int get totalCinemaCount => _totalCinemaCount;

  int get totalComingMovie => _totalComingMovie;

  int get totalHotMovie => _totalHotMovie;

  HotPlayMoviesModel(
      {int count,
      List<Movies> movies,
      int totalCinemaCount,
      int totalComingMovie,
      int totalHotMovie}) {
    _count = count;
    _movies = movies;
    _totalCinemaCount = totalCinemaCount;
    _totalComingMovie = totalComingMovie;
    _totalHotMovie = totalHotMovie;
  }

  HotPlayMoviesModel.fromJson(dynamic json) {
    _count = json["count"];
    if (json["movies"] != null) {
      _movies = [];
      json["movies"].forEach((v) {
        _movies.add(Movies.fromJson(v));
      });
    }
    _totalCinemaCount = json["totalCinemaCount"];
    _totalComingMovie = json["totalComingMovie"];
    _totalHotMovie = json["totalHotMovie"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["count"] = _count;
    if (_movies != null) {
      map["movies"] = _movies.map((v) => v.toJson()).toList();
    }
    map["totalCinemaCount"] = _totalCinemaCount;
    map["totalComingMovie"] = _totalComingMovie;
    map["totalHotMovie"] = _totalHotMovie;
    return map;
  }
}

